/**
 * Authentik OIDC Authentication Context
 *
 * AUTO-GENERATED BY PRISM - DO NOT EDIT
 * Provides authentication state and methods for OIDC flow with Authentik.
 */

import {
  createContext,
  useCallback,
  useContext,
  useEffect,
  useMemo,
  useState,
  type ReactNode,
} from 'react';

/** User data from backend */
export interface User {
  id: number;
  authentik_id: string;
  email: string;
  username: string;
  roles: string[];
  is_active: boolean;
}

/** Authentication context state and methods */
export interface AuthContextType {
  /** Current authenticated user (null if not logged in) */
  user: User | null;
  /** Whether authentication state is still loading */
  isLoading: boolean;
  /** Whether user is authenticated */
  isAuthenticated: boolean;
  /** User's roles for RBAC */
  roles: string[];
  /** Check if user has specific role */
  hasRole: (role: string) => boolean;
  /** Check if user has any of the specified roles */
  hasAnyRole: (...roles: string[]) => boolean;
  /** Initiate login flow (redirects to Authentik) */
  login: () => void;
  /** Logout and end session */
  logout: () => Promise<void>;
  /** Refresh user data from backend */
  refreshUser: () => Promise<void>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

interface AuthProviderProps {
  children: ReactNode;
}

/** API base URL from environment */
const API_BASE_URL = import.meta.env.VITE_API_URL || '';

export function AuthProvider({ children }: AuthProviderProps) {
  const [user, setUser] = useState<User | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  /** Fetch current user from backend */
  const fetchUser = useCallback(async (): Promise<User | null> => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/auth/me`, {
        credentials: 'include', // Include session cookie
      });

      if (response.ok) {
        return await response.json();
      }

      if (response.status === 401) {
        return null;
      }

      console.error('Failed to fetch user:', response.statusText);
      return null;
    } catch (error) {
      console.error('Error fetching user:', error);
      return null;
    }
  }, []);

  /** Initialize auth state on mount */
  useEffect(() => {
    const initAuth = async () => {
      setIsLoading(true);
      const userData = await fetchUser();
      setUser(userData);
      setIsLoading(false);
    };

    initAuth();
  }, [fetchUser]);

  /** Redirect to Authentik login */
  const login = useCallback(() => {
    // Redirect to backend login endpoint which initiates OIDC flow
    window.location.href = `${API_BASE_URL}/api/auth/login`;
  }, []);

  /** Logout and clear session */
  const logout = useCallback(async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/auth/logout`, {
        method: 'POST',
        credentials: 'include',
      });

      if (response.ok) {
        const data = await response.json();
        setUser(null);

        // Redirect to Authentik logout to end SSO session
        if (data.logout_url) {
          window.location.href = data.logout_url;
        }
      } else {
        // Fallback: clear local state and redirect to login
        setUser(null);
        window.location.href = '/login';
      }
    } catch (error) {
      console.error('Logout error:', error);
      setUser(null);
      window.location.href = '/login';
    }
  }, []);

  /** Refresh user data */
  const refreshUser = useCallback(async () => {
    const userData = await fetchUser();
    setUser(userData);
  }, [fetchUser]);

  /** Check if user has a specific role */
  const hasRole = useCallback(
    (role: string): boolean => {
      return user?.roles?.includes(role) ?? false;
    },
    [user]
  );

  /** Check if user has any of the specified roles */
  const hasAnyRole = useCallback(
    (...roles: string[]): boolean => {
      return roles.some((role) => user?.roles?.includes(role) ?? false);
    },
    [user]
  );

  const value = useMemo<AuthContextType>(
    () => ({
      user,
      isLoading,
      isAuthenticated: !!user,
      roles: user?.roles ?? [],
      hasRole,
      hasAnyRole,
      login,
      logout,
      refreshUser,
    }),
    [user, isLoading, hasRole, hasAnyRole, login, logout, refreshUser]
  );

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

/**
 * Hook to access authentication context
 *
 * @example
 * ```tsx
 * function Profile() {
 *   const { user, isAuthenticated, logout } = useAuth();
 *
 *   if (!isAuthenticated) {
 *     return <p>Please log in</p>;
 *   }
 *
 *   return (
 *     <div>
 *       <p>Welcome, {user.email}!</p>
 *       <button onClick={logout}>Logout</button>
 *     </div>
 *   );
 * }
 * ```
 */
export function useAuth(): AuthContextType {
  const context = useContext(AuthContext);

  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }

  return context;
}
